tinymce.PluginManager.add("vnp_image", function (t) {
	
	var WindowWidth = window.innerWidth*0.814;
	var WindowHeight = window.innerHeight*0.82;
	
    t.addButton("vnp_image", {
        icon: "browse",
        tooltip: "Insert/edit image",
        onclick: function() {
			
            // Open window
            t.windowManager.open({
                title: 'Media manager',
				width: WindowWidth,
                height: 535,
				url: VNP.FileBoxUrl,
                onsubmit: function(e) {
					alert('ok');
                    // Insert content when the window form is submitted
                    t.insertContent('Title: ' + e.data.title);
                }
            });
        },
        stateSelector: "img:not([data-mce-object])"
    })
	
	
	// Adds a menu item to the tools menu
    t.addMenuItem('vnp_image', {
        text: 'Add image',
        context: 'tools',
        onclick: function() {
            // Open window with a specific url
            t.windowManager.open({
                title: 'Media manager',
				width: WindowWidth,
                height: 535,
				url: VNP.FileBoxUrl,
                buttons: [{
                    text: 'Close',
                    onclick: 'close'
                }]
            });
        }
    });
});